/**
 * URL helpers for proxy middleware
 *
 * @public
 */
export class ApplicationURL {
    /**
     * Class constructor
     *
     * @public
     */
    constructor(_app) {
        this._app = _app;
    }
    /**
     * Application instance
     *
     * @public
     */
    get app() {
        return this._app();
    }
    /**
     * Node URL for dev
     *
     * @public
     */
    get dev() {
        return this.app.server.connection.url;
    }
    /**
     * Node URL for proxy
     *
     * @public
     */
    get proxy() {
        return this.app.hooks.filter(`dev.middleware.proxy.target`);
    }
}
