const name = label => `${label.charAt(0).toUpperCase()}${label.slice(1)}`;
export const transformEntry = ([slug, fontFamily]) => ({
    name: name(fontFamily.split(`,`).shift()),
    slug,
    fontFamily,
});
export const transform = fonts => Object.entries(fonts)
    .map(([k, v]) => [k, Array.isArray(v) ? v.join(`,`) : v])
    .map(transformEntry);
